mapper.insert:

    	INSERT INTO role
		(
			role_name
			, status
			, create_by
			, modify_by
			, create_date
			, modify_date
		)
		VALUES (
		#{roleName}
		, #{status}
		, #{createBy}
		, #{modifyBy}
		, to_char(NOW(), 'YYYYMMDD')
		, to_char(NOW(), 'YYYYMMDD')
		)

mapper.update:

UPDATE role
SET
	  role_name 		= #{roleName}
	, comp_status		= #{status}
	, comp_modify_by	= #{modifyBy}
	, comp_modify_date	= to_char(NOW(), 'YYYYMMDD')
WHERE id 			= #{id}


		update role
        set
            status = #{status}
        where id   = #{id}
