mapper.insert:

    	INSERT INTO tb_company
		(
			comp_nm
			, comp_contact
			, comp_email
			, comp_address
			, comp_desc
			, comp_status
			, comp_create_by
			, comp_modify_by
			, comp_create_date
			, comp_modify_date
		)
		VALUES (
		#{name}
		, #{contact}
		, #{email}
		, #{address}
		, COALESCE('', #{description} )
		, #{status}
		, #{createBy}
		, #{modifyBy}
		, to_char(NOW(), 'YYYYMMDD')
		, to_char(NOW(), 'YYYYMMDD')
		)

mapper.update:

UPDATE tb_company
SET
	  comp_nm 			= #{name}
	, comp_contact		= #{contact}
	, comp_email		= #{email}
	, comp_address		= #{address}
	, comp_desc 		= COALESCE(#{description},'')
	, comp_status		= #{status}
	, comp_modify_by	= #{modifyBy}
	, comp_modify_date	= to_char(NOW(), 'YYYYMMDD')
WHERE comp_id 			= #{id}


		update tb_company
        set
            comp_status = #{status}
        where comp_id   = #{id}
